oxy_webhelp_navlinks = {"topic_r41_22b_tz-d318e138":{"topics":[{"title":"Content Reference (conref)","shortdesc":"<p class=\"shortdesc\">The content reference mechanism pulls a content block to another topic.<\/p>","href":"topics\/conref.html","attributes":{"data-id":"conref"},"menu":{"hasChildren":false},"tocID":"conref-d318e149","topics":[]},{"title":"Key Reference (keyref)","shortdesc":"<p class=\"shortdesc\">The key reference mechanism has been developed to decouple information.<\/p>","href":"topics\/keyref.html","attributes":{"data-id":"keyref"},"menu":{"hasChildren":false},"tocID":"keyref-d318e160","topics":[]},{"title":"Content Key Reference (conkeyref)","shortdesc":"<p class=\"shortdesc\">The content key reference mechanism combines the content reference mechanism with the     key reference mechanism.<\/p>","href":"topics\/conkeyref.html","attributes":{"data-id":"conkeyref"},"menu":{"hasChildren":false},"tocID":"conkeyref-d318e171","topics":[]},{"title":"Content Reference End (conrefend)","shortdesc":"<p class=\"shortdesc\">The content reference end mechansim specifies an element, that is the last element of a     content reference range. It is used together with a content reference or content key reference,     that specifies the first element.<\/p>","href":"topics\/conrefend.html","attributes":{"data-id":"conrefend"},"menu":{"hasChildren":false},"tocID":"conrefend-d318e182","topics":[]},{"title":"Relationship Table (reltable)","shortdesc":"<p class=\"shortdesc\"><a class=\"xref\" href=\"https:\/\/www.oxygenxml.com\/dita\/1.3\/specs\/langRef\/base\/reltable.html?hl=relationship%2Ctable\" target=\"_blank\" rel=\"external noopener\">Relationship Tables<\/a> externalize links and decouple     topics from each other, because the topics themselves do not contain links to other topics, that     could break, if the target topics are missing.<\/p>","href":"topics\/reltable.html","attributes":{"data-id":"reltable"},"menu":{"hasChildren":false},"tocID":"reltable-d318e193","topics":[]},{"title":"Content Reference Push (conref push)","shortdesc":"<p class=\"shortdesc\">The content reference push mechanism is the opposite of a content     reference.<\/p>","href":"topics\/conrefpush.html","attributes":{"data-id":"conrefpush"},"menu":{"hasChildren":false},"tocID":"conrefpush-d318e206","topics":[]},{"title":"Filtering (DITAVAL)","shortdesc":"<p class=\"shortdesc\">A DITAVAL file <em class=\"ph i\">includes<\/em>, <em class=\"ph i\">excludes<\/em> or <em class=\"ph i\">flags<\/em> elements with filter     attributes. DITAVAL files can be passed into the build process by the       <code class=\"ph codeph\">dita.input.valfile<\/code> property or by <a class=\"xref\" href=\"https:\/\/www.oxygenxml.com\/dita\/1.3\/specs\/langRef\/base\/ditavalref.html\" target=\"_blank\" rel=\"external noopener\"><code class=\"keyword markupname xmlelement\">&lt;ditavalref&gt;<\/code><\/a>.<\/p>","href":"topics\/ditaval.html","attributes":{"data-id":"ditaval"},"menu":{"hasChildren":false},"tocID":"ditaval-d318e217","topics":[]},{"title":"Subject Scheme","shortdesc":"<p class=\"shortdesc\">The Subject Scheme Map is a specialized DITA Map that defines allowed attribute     values.<\/p>","href":"topics\/subjectScheme.html","attributes":{"data-id":"subjectscheme"},"menu":{"hasChildren":false},"tocID":"subjectscheme-d318e244","topics":[]},{"title":"Key Scopes","shortdesc":"<p class=\"shortdesc\">Keys can be limited to a specific scope by defining key scopes. This mechanism can be     used to fill <em class=\"ph i\">template<\/em> topics, that are referenced multiple times, with different     data.<\/p>","href":"topics\/key-scopes.html","attributes":{"data-id":"key-scopes"},"menu":{"hasChildren":false},"tocID":"key-scopes-d318e255","topics":[]}]}};